operations.reducer:
state.id = action.input.id;
state.customerId = action.input.customerId;
state.customerName = action.input.customerName || null;
state.serviceOfferingId = action.input.serviceOfferingId;
state.serviceOfferingTitle = action.input.serviceOfferingTitle || null;
state.resourceTemplateId = action.input.resourceTemplateId;
state.selectedTierId = action.input.selectedTierId;
state.status = "PENDING";
state.autoRenew = true;
state.createdAt = action.input.createdAt;
state.lastModified = action.input.lastModified;

state.status = "ACTIVE";
state.startDate = action.input.startDate;
state.currentPeriodStart = action.input.currentPeriodStart;
state.currentPeriodEnd = action.input.currentPeriodEnd;
state.lastModified = action.input.lastModified;

operations.schema:
input ActivateSubscriptionInput {
    startDate: DateTime!
    currentPeriodStart: DateTime!
    currentPeriodEnd: DateTime!
    lastModified: DateTime!
}

operations.reducer:
state.autoRenew = false;
state.cancelledAt = action.input.cancelledAt;
state.cancelEffectiveDate = action.input.cancelEffectiveDate || null;
state.cancellationReason = action.input.reason || null;
state.lastModified = action.input.lastModified;

operations.schema:
input CancelSubscriptionInput {
    cancelledAt: DateTime!
    cancelEffectiveDate: DateTime
    reason: String
    lastModified: DateTime!
}

input ChangeTierInput {
    newTierId: OID!
    lastModified: DateTime!
}

operations.reducer:
state.pricing = {
    amount: action.input.amount,
    currency: action.input.currency,
    billingCycle: action.input.billingCycle,
    setupFee: action.input.setupFee || null
};
state.lastModified = action.input.lastModified;

state.selectedAddons.push({
    id: action.input.id,
    optionGroupId: action.input.optionGroupId,
    addedAt: action.input.addedAt
});
state.lastModified = action.input.lastModified;

operations.schema:
input AddAddonInput {
    id: OID!
    optionGroupId: OID!
    addedAt: DateTime!
    lastModified: DateTime!
}

operations.reducer:
const addonIndex = state.selectedAddons.findIndex(a => a.id === action.input.id);
if (addonIndex !== -1) {
    state.selectedAddons.splice(addonIndex, 1);
}
state.lastModified = action.input.lastModified;

operations.schema:
input RemoveAddonInput {
    id: OID!
    lastModified: DateTime!
}

operations.reducer:
const existingIndex = state.facetSelections.findIndex(fs => fs.categoryKey === action.input.categoryKey);
if (existingIndex !== -1) {
    state.facetSelections[existingIndex] = {
        id: action.input.id,
        categoryKey: action.input.categoryKey,
        selectedOptionId: action.input.selectedOptionId
    };
} else {
    state.facetSelections.push({
        id: action.input.id,
        categoryKey: action.input.categoryKey,
        selectedOptionId: action.input.selectedOptionId
    });
}
state.lastModified = action.input.lastModified;

operations.schema:
input SetFacetSelectionInput {
    id: OID!
    categoryKey: String!
    selectedOptionId: String!
    lastModified: DateTime!
}

operations.reducer:
const selectionIndex = state.facetSelections.findIndex(fs => fs.categoryKey === action.input.categoryKey);
if (selectionIndex !== -1) {
    state.facetSelections.splice(selectionIndex, 1);
}
state.lastModified = action.input.lastModified;

operations.schema:
input RemoveFacetSelectionInput {
    categoryKey: String!
    lastModified: DateTime!
}

operations.reducer:
state.nextBillingDate = action.input.nextBillingDate || null;
state.projectedBillAmount = action.input.projectedBillAmount || null;
state.projectedBillCurrency = action.input.projectedBillCurrency || null;
state.lastModified = action.input.lastModified;

operations.schema:
input UpdateBillingProjectionInput {
    nextBillingDate: DateTime
    projectedBillAmount: Amount_Money
    projectedBillCurrency: Currency
    lastModified: DateTime!
}

operations.reducer:
if (action.input.customerName !== undefined) {
    state.customerName = action.input.customerName || null;
}
if (action.input.serviceOfferingTitle !== undefined) {
    state.serviceOfferingTitle = action.input.serviceOfferingTitle || null;
}
state.lastModified = action.input.lastModified;

operations.schema:
input SetCachedSnippetsInput {
    customerName: String
    serviceOfferingTitle: String
    lastModified: DateTime!
}
